            padding: 2px 8px;
            background: rgba(255, 255, 255, 0.1);
            border-radius: 4px;
        }

        .status-bar {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
            gap: 15px;
            margin-bottom: 20px;
            padding: 15px;
            background: rgba(0, 0, 0, 0.2);
            border-radius: 10px;
        }
        .status-item {
            text-align: center;
            padding: 10px;
            background: rgba(255, 255, 255, 0.08);
            border-radius: 8px;
        }
        .status-number { font-size: 1.2em; font-weight: bold; margin-bottom: 5px; }
        .status-label { font-size: 0.8em; opacity: 0.8; }

        /* 统一蓝色系 */
        .success { color: #3ba4ff; }
        .error { color: #ff4757; }
        .total { color: #00a8ff; }
        .progress { color: #4fbef6; }
        .depth { color: #6aa4ff; }
        .urls { color: #26de81; }

        .controls { display: flex; gap: 10px; margin-bottom: 15px; flex-wrap: wrap; }

        .boton-elegante {
            padding: 12px 22px;
            border: 2px solid #2c2c2c;
            background-color: #1a1a1a;
            color: #ffffff;
            font-size: 14px;
            cursor: pointer;
            border-radius: 30px;
            transition: all 0.4s ease;
            outline: none;
            position: relative;
            overflow: hidden;
            font-weight: 600;
        }
        .boton-elegante::after {
            content: "";
            position: absolute;
            top: 0; left: 0;
            width: 100%; height: 100%;
            background: radial-gradient(circle, rgba(255,255,255,0.25) 0%, rgba(255,255,255,0) 70%);
            transform: scale(0);
            transition: transform 0.5s ease;
        }
        .boton-elegante:hover::after { transform: scale(4); }
        .boton-elegante:hover { border-color: #666666; background: #292929; }
        .boton-elegante:disabled { opacity: 0.5; cursor: not-allowed; }

        /* 变体 */
        .btn-start {
            border-color: #1f6feb;
            background: linear-gradient(45deg, #37b9ff, #0ea5e9);
            color: #001b36;
        }
        .btn-start:hover {
            border-color: #60a5fa;
            background: linear-gradient(45deg, #5cc8ff, #28b0ff);
            color: #001b36;
        }
        .btn-secondary-modern { border-color: #2c2c2c; background-color: #1a1a1a; color: #e5e7eb; }
        .btn-danger-modern { border-color: #7f1d1d; background: linear-gradient(45deg, #ef4444, #b91c1c); color: #fff; }

        /* 保留 .btn 用于弹窗按钮 */
        .btn {
            padding: 10px 20px;
            border: none;
            border-radius: 8px;
            cursor: pointer;
            font-size: 14px;
            font-weight: 500;
            transition: all 0.3s ease;
        }
        .btn-primary { background: linear-gradient(45deg, #1f6feb, #37b9ff); color: #fff; }
        .btn-secondary { background: rgba(255,255,255,0.2); color: #fff; border: 1px solid rgba(255,255,255,0.3); }
        .btn-danger { background: linear-gradient(45deg, #ff4757, #ff3742); color: #fff; }
        .btn:hover { transform: translateY(-2px); box-shadow: 0 5px 15px rgba(0,0,0,0.3); }
        .btn:disabled { opacity: 0.5; cursor: not-allowed; transform: none; }

        .progress-section { margin-bottom: 20px; padding: 15px; background: rgba(0, 0, 0, 0.2); border-radius: 10px; }
        .progress-bar-container { width: 100%; height: 20px; background: rgba(255,255,255,0.1); border-radius: 10px; overflow: hidden; margin-top: 10px; }
        .progress-bar { height: 100%; background: linear-gradient(45deg, #00d4aa, #00a8ff); width: 0%; transition: width 0.3s ease; border-radius: 10px; }

        .results-section { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 15px; margin-bottom: 20px; }
        .result-category { background: rgba(0, 0, 0, 0.2); border-radius: 10px; padding: 15px; max-height: 300px; overflow-y: auto; }
        .result-category h3 { margin-bottom: 10px; color: #1f6feb; font-size: 1.1em; border-bottom: 1px solid rgba(48, 90, 160, 0.35); padding-bottom: 5px; }
        .result-list { list-style: none; }
        .result-item { padding: 5px 0; border-bottom: 1px solid rgba(255, 255, 255, 0.1); font-size: 12px; word-break: break-all; }
        .result-item:last-child { border-bottom: none; }
        .result-item.new { background: rgba(59, 164, 255, 0.15); padding: 5px; border-radius: 4px; animation: highlight 2s ease-out; }
        @keyframes highlight { 0% { background: rgba(59,164,255,0.35); } 100% { background: rgba(59,164,255,0.15); } }

        .loading { text-align: center; padding: 30px; font-size: 1.1em; }
        .spinner {
            display: inline-block;
            width: 40px; height: 40px;
            border: 4px solid rgba(48, 90, 160, 0.35);
            border-radius: 50%;
            border-top-color: #1f6feb;
            animation: spin 1s ease-in-out infinite;
            margin-bottom: 15px;
        }
        @keyframes spin { to { transform: rotate(360deg); } }

        .log-section { background: rgba(0, 0, 0, 0.3); border-radius: 10px; padding: 15px; max-height: 200px; overflow-y: auto; font-family: 'Courier New', monospace; font-size: 11px; }
        .log-entry { margin-bottom: 5px; padding: 2px 0; }
        .log-entry.info { color: #00a8ff; }
        .log-entry.success { color: #3ba4ff; } /* ✅ 成功信息蓝色 */
        .log-entry.warning { color: #ffa502; }
        .log-entry.error { color: #ff4757; }

        /* 完成/成功态文本（若脚本赋予类名） */
        #progressText.success, #progressText.done { color: #3ba4ff; }

        /* 自定义滚动条 */
        ::-webkit-scrollbar { width: 8px; }
        ::-webkit-scrollbar-track { background: rgba(255, 255, 255, 0.1); border-radius: 4px; }
        ::-webkit-scrollbar-thumb { background: #1f6feb; border-radius: 4px; transition: background 0.2s ease; }
        ::-webkit-scrollbar-thumb:hover { background: #37b9ff; }

/* 弹窗样式（保留原有交互） */
        .modal {
            position: fixed;
            z-index: 10000;
            left: 0; top: 0;
            width: 100%; height: 100%;
            background-color: rgba(0, 0, 0, 0.7);
            display: flex; justify-content: center; align-items: center;
        }
        .modal-content {
            background: linear-gradient(135deg, #1f6feb 0%, #0ea5e9 100%);
            border-radius: 15px;
            padding: 0;
            width: 90%; max-width: 500px;
            box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
            animation: modalSlideIn 0.3s ease;
        }
        @keyframes modalSlideIn {
            from { opacity: 0; transform: translateY(-50px) scale(0.9); }
            to { opacity: 1; transform: translateY(0) scale(1); }
        }
        .modal-header {
            padding: 20px 25px 15px;
            border-bottom: 1px solid rgba(255, 255, 255, 0.25);
            display: flex; justify-content: space-between; align-items: center;
        }
        .modal-header h3 { margin: 0; color: #fff; font-size: 1.3em; }
        .close {
            color: #fff; font-size: 28px; font-weight: bold; cursor: pointer; line-height: 1;
            opacity: 0.7; transition: opacity 0.2s;
        }
        .close:hover { opacity: 1; }
        .modal-body { padding: 25px; }
        .modal-body p { margin: 0 0 20px 0; color: #fff; opacity: 0.9; }
        .export-options { display: flex; flex-direction: column; gap: 15px; }
        .export-option {
            display: flex; align-items: center; gap: 15px;
            padding: 15px 20px; text-align: left; border-radius: 10px; transition: all 0.3s ease;
            background: rgba(255, 255, 255, 0.12); border: 1px solid rgba(255, 255, 255, 0.25);
        }
        .export-option:hover { background: rgba(255, 255, 255, 0.22); transform: translateY(-2px); box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3); }
        .export-option .icon { font-size: 24px; min-width: 30px; }
        .export-option .text { font-weight: 600; font-size: 16px; color: #fff; }
        .export-option .desc { font-size: 12px; opacity: 0.8; color: #fff; margin-left: auto; }

