        }

        /* 进度条 */
        .progress-container {
            margin-top: 10px;
        }

        .progress-text {
            font-size: 12px;
            margin-bottom: 5px;
            color: #ccc;
        }

        .progress-bar-container {
            background: rgba(0, 0, 0, 0.5);
            border-radius: 10px;
            height: 6px;
            overflow: hidden;
        }

        .progress-bar {
            background: linear-gradient(45deg, #00d4aa, #0ea5e9);
            height: 100%;
            width: 0%;
            transition: width 0.3s ease;
        }

        /* API测试区域 */
        .api-test-section {
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 8px;
            padding: 15px;
            margin-bottom: 15px;
            transition: all 0.3s;
        }

        .api-test-section:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.2);
        }

        .test-controls {
            display: flex;
            gap: 10px;
            margin-bottom: 10px;
        }

        .test-controls select {
            flex: 1;
            padding: 8px;
            border-radius: 6px;
            background: rgba(0, 0, 0, 0.5);
            color: #fff;
            border: 1px solid #444;
            font-size: 12px;
        }

        /* 结果显示区域 */
        .results-container {
            max-height: 300px;
            overflow-y: auto;
            overflow-x: hidden;
        }

        .category {
            margin-bottom: 15px;
            background: rgba(0, 0, 0, 0.2);
            border: 1px solid rgba(255, 255, 255, 0.1);
            border-radius: 8px;
            padding: 12px;
            transition: all 0.3s;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .category:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.2);
            background: rgba(0, 0, 0, 0.3);
        }

        .category-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            cursor: pointer;
            margin-bottom: 8px;
        }

        .category-title {
            font-weight: 600;
            font-size: 14px;
            color: #fff;
        }

        .count-badge {
            background: linear-gradient(45deg, #60a5fa, #3b82f6);
            color: #0b1220;
            padding: 2px 8px;
            border-radius: 12px;
            font-size: 12px;
            font-weight: 600;
        }

        .category-content {
            max-height: 120px;
            overflow-y: auto;
            overflow-x: hidden;
            font-size: 12px;
            width: 100%;
            box-sizing: border-box;
        }

        .category-content.collapsed {
            display: none;
        }

        .item {
            padding: 6px 10px;
            margin: 3px 0;
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 4px;
            word-break: break-all;
            cursor: pointer;
            transition: all 0.3s ease;
            color: #ccc;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .item:hover {
            background: rgba(255, 255, 255, 0.1);
            border-color: #3b82f6;
            color: #fff;
            transform: translateX(3px);
        }

        .item::after {
            content: '';
            position: absolute;
            left: -100%;
            top: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
            transition: all 0.5s;
        }

        .item:hover::after {
            left: 100%;
        }

        .item.copied {
            background: rgba(59, 130, 246, 0.16);
            border-color: #3b82f6;
            color: #fff;
        }

        /* 加载状态 */
        .loading {
            text-align: center;
            padding: 20px;
            color: #00d4aa;
        }

        .loading::after {
            content: '';
            display: inline-block;
            width: 20px;
            height: 20px;
            border: 2px solid rgba(0, 212, 170, 0.3);
            border-top: 2px solid #00d4aa;
            border-radius: 50%;
            animation: spin 1s linear infinite;
            margin-left: 10px;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        /* 统计信息 */
        .stats {
            text-align: center;
            margin-top: 15px;
            padding: 10px;
            background: rgba(0, 0, 0, 0.3);
            border-radius: 6px;
            font-size: 12px;
            color: #ccc;
            transition: all 0.3s;
            border: 1px solid rgba(255, 255, 255, 0.05);
        }

        .stats:hover {
            background: rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.1);
            transform: translateY(-2px);
        }

        /* 滚动条样式 */
        /* 隐藏数字输入框的加减按钮 */
        input[type="number"]::-webkit-outer-spin-button,
        input[type="number"]::-webkit-outer-spin-button,
        input[type="number"]::-webkit-inner-spin-button {
            -webkit-appearance: none;
            appearance: none;
            margin: 0;
        }

        input[type="number"] {
            -webkit-appearance: textfield;
            -moz-appearance: textfield;
            appearance: textfield;
        }

        /* 滚动条样式 */
        ::-webkit-scrollbar {
            width: 6px;
        }

        ::-webkit-scrollbar-track {
            background: transparent;
            border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb {
            background: rgba(255, 255, 255, 0.6);
            border-radius: 4px;
        }
        ::-webkit-scrollbar-thumb:hover {
            background: rgba(255, 255, 255, 0.8);
        }

        /* 模态框样式 */
        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.8);
            z-index: 1000;
        }

        .modal-content {
            position: relative;
            width: 90%;
            max-width: 600px;
            max-height: 80%;
            background: #1a1a2e;
            margin: 50px auto;
            padding: 20px;
            border-radius: 8px;
            border: 1px solid #444;
            overflow-y: auto;
        }

        .modal-close {
            position: absolute;
            top: 10px;
            right: 15px;
            background: none;
            border: none;
            color: #fff;
            font-size: 24px;
            cursor: pointer;
        }

        .modal h3 {
            color: #00d4aa;
            margin-bottom: 15px;
        }

        /* 导出弹窗特殊样式 */
        .export-modal-content {
            max-width: 500px;
        }

        .export-modal-body {
