        }

        .header-input-group input {
            flex: 1;
            background: #333;
            border: 1px solid #555;
            color: #fff;
            padding: 6px 10px;
            border-radius: 3px;
        }

        .header-input-group input[readonly] {
            background: #444;
            cursor: default;
        }

        /* JS注入脚本列表样式 */
        .header-input-group span {
            flex: 0 1 150px;
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
            color: #fff;
