        }

        /* 统一按钮样式 */
        .inject-btn, .modify-btn, .delete-btn {
            padding: 6px 12px;
            border: 1px solid #555;
            border-radius: 4px;
            color: #fff;
            cursor: pointer;
            font-size: 12px;
            transition: all 0.2s ease;
            min-width: 50px;
            height: 28px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
