        }

        /* 结果显示区域 */
        .results-container {
            max-height: 300px;
            overflow-y: auto;
            overflow-x: hidden;
        }

        .category {
            margin-bottom: 15px;
            background: rgba(0, 0, 0, 0.2);
            border: 1px solid rgba(255, 255, 255, 0.1);
            border-radius: 8px;
            padding: 12px;
            transition: all 0.3s;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .category:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.2);
            background: rgba(0, 0, 0, 0.3);
        }

        .category-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            cursor: pointer;
            margin-bottom: 8px;
        }

        .category-title {
            font-weight: 600;
            font-size: 14px;
            color: #fff;
        }

        .count-badge {
            background: linear-gradient(45deg, #60a5fa, #3b82f6);
            color: #0b1220;
            padding: 2px 8px;
            border-radius: 12px;
            font-size: 12px;
            font-weight: 600;
        }

        .category-content {
            max-height: 120px;
            overflow-y: auto;
            overflow-x: hidden;
            font-size: 12px;
            width: 100%;
            box-sizing: border-box;
        }

        .category-content.collapsed {
            display: none;
        }

        .item {
            padding: 6px 10px;
            margin: 3px 0;
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 4px;
            word-break: break-all;
            cursor: pointer;
            transition: all 0.3s ease;
            color: #ccc;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .item:hover {
            background: rgba(255, 255, 255, 0.1);
            border-color: #3b82f6;
            color: #fff;
            transform: translateX(3px);
        }

        .item::after {
            content: '';
            position: absolute;
            left: -100%;
            top: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
            transition: all 0.5s;
        }

        .item:hover::after {
            left: 100%;
        }

        .item.copied {
            background: rgba(59, 130, 246, 0.16);
