        @keyframes moveDot {
            0%, 100% { top: 10%; right: 10%; }
            25% { top: 10%; right: calc(100% - 35px); }
            50% { top: calc(100% - 30px); right: calc(100% - 35px); }
            75% { top: calc(100% - 30px); right: 10%; }
        }

        .card {
            z-index: 1;
            width: 100%;
            height: 100%;
            min-height: 648px;
            border-radius: 10px;
            background: rgb(45, 45, 45);
            position: relative;
            color: #fff;
            overflow: hidden;
            box-shadow: 0px 0px 10px 5px rgba(0, 0, 0, 0.705);
            transition: all 0.4s;
        }

        .card:hover {
            border-radius: 15px;
            box-shadow: 0px 0px 15px 8px rgba(0, 0, 0, 0.8);
            background: rgb(30, 30, 30);
        }

        /* 内容切换效果 */
        .card .first-content {
            position: relative;
            height: 100%;
            width: 100%;
            z-index: 1;
            transition: all 0.4s;
        }

        .card::after {
            content: "";
            position: absolute;
            width: 260px;
            height: 200px;
            left: -30%;
            top: -30%;
            background: #ffffff;
            filter: blur(60px);
            opacity: 0.4;
            z-index: 0;
            pointer-events: none;
            transition: all 0.4s;
        }

        .card:hover::after {
            opacity: 0.6;
            width: 280px;
            height: 220px;
            filter: blur(70px);
        }







