            0%, 100% { top: 10%; right: 10%; }
            25% { top: 10%; right: calc(100% - 35px); }
            50% { top: calc(100% - 30px); right: calc(100% - 35px); }
            75% { top: calc(100% - 30px); right: 10%; }
        }

        .card {
            z-index: 1;
            width: 100%;
            height: 100%;
            min-height: 648px;
            border-radius: 10px;
            background: rgb(45, 45, 45);
            position: relative;
            color: #fff;
