        .nav-tab {
            flex: 1;
            padding: 8px 12px;
            text-align: center;
            font-size: 12px;
            font-weight: 500;
            cursor: pointer;
            border-radius: 6px;
            transition: all 0.3s ease;
            color: #888;
            position: relative;
            overflow: hidden;
        }

        .nav-tab.active {
            background: rgba(255, 255, 255, 0.08);
            color: #fff;
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
            border: 1px solid rgba(255, 255, 255, 0.12);
        }

        .nav-tab:hover {
            color: #fff;
            background: rgba(255, 255, 255, 0.05);
            transform: translateY(-1px);
        }

        .nav-tab::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 50%;
            width: 0;
            height: 2px;
            background: linear-gradient(90deg, transparent, #3b82f6, transparent);
            transition: all 0.3s;
        }

        .nav-tab:hover::after {
            width: 80%;
            left: 10%;
        }

        .nav-tab.active::after {
