        }

        /* 设置页面样式 */
        .headers-controls {
            margin-bottom: 15px;
        }

        .header-input-group {
            display: flex;
            gap: 8px;
            margin-bottom: 10px;
            align-items: flex-start;
        }

        .header-input-group input[type="text"] {
            padding: 8px;
            background: rgba(0, 0, 0, 0.5);
            border: 1px solid #444;
            border-radius: 6px;
            color: #fff;
            font-size: 12px;
        }

        .header-key-input {
            flex: 1;
            min-width: 120px;
        }

        .header-value-input {
