        .headers-actions {
            display: flex;
            gap: 10px;
            margin-bottom: 10px;
        }

        .headers-actions .action-button {
            flex: 1;
        }

        #headersContainer {
            margin-bottom: 15px;
        }

        .header-input-group input[type="text"]:focus {
            outline: none;
            border-color: #00d4aa;
            box-shadow: 0 0 5px rgba(0, 212, 170, 0.3);
        }

        /* 脚本项布局调整 */
        .script-item {
            display: flex;
            align-items: center;
            padding: 8px;
            margin: 4px 0;
            background: rgba(255, 255, 255, 0.05);
            border-radius: 4px;
            border: 1px solid rgba(255, 255, 255, 0.1);
        }

        .script-name {
            flex: 1;
            color: #fff;
