        .action-button .border-tl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 8px 0 0 0;
            top: 0;
            left: 0;
        }

        .action-button .border-tr {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 0 8px 0 0;
            top: 0;
            right: 0;
