            margin-bottom: 8px;
            padding: 8px;
            background: #2a2a2a;
            border-radius: 4px;
            gap: 8px;
        }

        .header-input-group input {
            flex: 1;
            background: #333;
            border: 1px solid #555;
            color: #fff;
            padding: 6px 10px;
            border-radius: 3px;
        }

        .header-input-group input[readonly] {
            background: #444;
            cursor: default;
        }

        /* JS注入脚本列表样式 */
        .header-input-group span {
            flex: 0 1 150px;
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
            color: #fff;
            font-size: 14px;
        }

        /* 统一按钮样式 */
        .inject-btn, .modify-btn, .delete-btn {
            padding: 6px 12px;
            border: 1px solid #555;
            border-radius: 4px;
            color: #fff;
            cursor: pointer;
            font-size: 12px;
            transition: all 0.2s ease;
            min-width: 50px;
            height: 28px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
            background: #4a4a4a;
        }

        .inject-btn:hover, .modify-btn:hover, .delete-btn:hover {
            background: #5a5a5a;
        }

* {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            width: 480px;
            min-height: 650px;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: rgb(30, 30, 30);
            color: #fff;
            overflow-x: hidden;
        }

        .main-container {
            width: 100%;
            min-height: 650px;
            border-radius: 10px;
            padding: 1px;
            background: rgb(30, 30, 30);
            position: relative;
        }



        @keyframes moveDot {
            0%, 100% { top: 10%; right: 10%; }
            25% { top: 10%; right: calc(100% - 35px); }
            50% { top: calc(100% - 30px); right: calc(100% - 35px); }
            75% { top: calc(100% - 30px); right: 10%; }
        }

        .card {
            z-index: 1;
            width: 100%;
            height: 100%;
            min-height: 648px;
            border-radius: 10px;
            background: rgb(45, 45, 45);
            position: relative;
            color: #fff;
            overflow: hidden;
            box-shadow: 0px 0px 10px 5px rgba(0, 0, 0, 0.705);
            transition: all 0.4s;
        }

        .card:hover {
            border-radius: 15px;
            box-shadow: 0px 0px 15px 8px rgba(0, 0, 0, 0.8);
            background: rgb(30, 30, 30);
        }

        /* 内容切换效果 */
        .card .first-content {
            position: relative;
            height: 100%;
            width: 100%;
            z-index: 1;
            transition: all 0.4s;
        }

        .card::after {
            content: "";
            position: absolute;
            width: 260px;
            height: 200px;
            left: -30%;
            top: -30%;
            background: #ffffff;
            filter: blur(60px);
            opacity: 0.4;
            z-index: 0;
            pointer-events: none;
            transition: all 0.4s;
        }

        .card:hover::after {
            opacity: 0.6;
            width: 280px;
            height: 220px;
            filter: blur(70px);
        }







        .bottoml {
            bottom: 10%;
        }

        .leftl {
            left: 10%;
            width: 1px;
            height: 100%;
            background: linear-gradient(180deg, #747474 30%, #222424 70%);
        }

        .rightl {
            right: 10%;
            width: 1px;
            height: 100%;
        }

        /* 标题区域 */
        .header {
            text-align: center;
            padding: 12px 15px 10px;
            position: relative;
            z-index: 10;
        }

        .header h1 {
            font-size: 20px;
            font-weight: bolder;
            background: linear-gradient(45deg, rgb(30, 30, 30) 4%, #fff, rgb(30, 30, 30));
            background-clip: text;
            -webkit-background-clip: text;
            color: transparent;
            margin-bottom: 3px;
        }

        .author-info {
            font-size: 11px;
            color: #888;
            opacity: 0.8;
        }

        /* 横向导航 */
        .nav-tabs {
            display: flex;
            justify-content: center;
            margin: 0 15px 20px;
            background: rgba(255, 255, 255, 0.04);
            border: 1px solid rgba(255, 255, 255, 0.08);
            border-radius: 10px;
            padding: 6px;
            position: relative;
            z-index: 10;
        }

        .nav-tab {
            flex: 1;
            padding: 8px 12px;
            text-align: center;
            font-size: 12px;
            font-weight: 500;
            cursor: pointer;
            border-radius: 6px;
            transition: all 0.3s ease;
            color: #888;
            position: relative;
            overflow: hidden;
        }

        .nav-tab.active {
            background: rgba(255, 255, 255, 0.08);
            color: #fff;
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
            border: 1px solid rgba(255, 255, 255, 0.12);
        }

        .nav-tab:hover {
            color: #fff;
            background: rgba(255, 255, 255, 0.05);
            transform: translateY(-1px);
        }

        .nav-tab::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 50%;
            width: 0;
            height: 2px;
            background: linear-gradient(90deg, transparent, #3b82f6, transparent);
            transition: all 0.3s;
        }

        .nav-tab:hover::after {
            width: 80%;
            left: 10%;
        }

        .nav-tab.active::after {
            width: 90%;
            left: 5%;
            background: linear-gradient(90deg, transparent, #00d4aa, transparent);
        }

        /* 页面内容区域 */
        .page-content {
            padding: 0 15px;
            position: relative;
            z-index: 10;
        }

        .page {
            display: none;
        }

        .page.active {
            display: block;
        }

        .action-button {
            position: relative;
            cursor: pointer;
            padding: 12px 16px;
            text-align: center;
            display: inline-flex;
            justify-content: center;
            align-items: center;
            font-size: 14px;
            font-weight: 600;
            color: #fff;
            border-radius: 10px;
            border: 1px solid rgba(255, 255, 255, 0.2);
            background: rgba(31, 41, 55, 0.3);
            backdrop-filter: blur(12px);
            transition: transform 0.3s ease, box-shadow 0.3s ease, background 0.3s ease;
            width: 100%;
            margin-bottom: 10px;
            overflow: hidden;
            will-change: transform;
        }

        .action-button:focus {
            outline: 2px solid white;
            outline-offset: 4px;
        }

        .action-button span.text {
            position: relative;
            z-index: 20;
        }

        .action-button .shine {
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            width: 42px;
            background: rgba(255, 255, 255, 0.2);
            filter: blur(0);
            transform: skew(-13deg) translateX(-120%);
            transition: transform 1s ease;
            z-index: 10;
            pointer-events: none;
        }

        .action-button:hover .shine {
            transform: skew(-13deg) translateX(120%);
        }
        .action-button:hover {
            transform: scale(1.08);
            box-shadow: 0 20px 25px -5px rgba(107, 114, 128, 0.5);
        }

        .action-button .border-tl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 8px 0 0 0;
            top: 0;
            left: 0;
        }

        .action-button .border-tr {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 0 8px 0 0;
            top: 0;
            right: 0;
        }

        .action-button:hover .border-tr {
            height: 90%;
        }

        .action-button .border-bl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-top: none;
            position: absolute;
            height: 90%;
            border-radius: 0 0 0 8px;
            left: 0;
            bottom: 0;
        }

        .action-button:hover .border-bl {
            height: 90%;
        }

        .action-button .border-br {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-top: none;
            position: absolute;
            height: 20%;
            border-radius: 0 0 8px 0;
            right: 0;
            bottom: 0;
        }
        /* 弱化霓虹装饰 */
        .action-button .border-tl,
        .action-button .border-tr,
        .action-button .border-bl,
        .action-button .border-br {
            display: none !important;
        }

        /* 扫描按钮特殊样式 */
        .scan-button {
            background: #3b82f6;
            border-color: rgba(59, 130, 246, 0.5);
        }
        .scan-button:hover {
            background: #2563eb;
        }
        .scan-button.scanning {
            animation: bluepulse 2s infinite;
        }
        @keyframes bluepulse {
            0% { box-shadow: 0 0 5px rgba(59,130,246,0.25); }
            50% { box-shadow: 0 0 18px rgba(59,130,246,0.45); }
            100% { box-shadow: 0 0 5px rgba(59,130,246,0.25); }
        }

        /* 深度扫描配置 */
        .deep-scan-config {
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 8px;
            padding: 15px;
            margin-bottom: 15px;
            transition: all 0.3s;
        }

        .deep-scan-config:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.2);
        }

        .config-title {
            font-weight: 600;
            color: #fff;
            margin-bottom: 10px;
            font-size: 14px;
        }

        .config-options {
            display: flex;
            gap: 15px;
            margin-bottom: 10px;
            flex-wrap: wrap;
        }

        .config-option {
            display: flex;
            align-items: center;
            gap: 5px;
            font-size: 12px;
            color: #ccc;
        }

        .config-option input[type="checkbox"] {
            accent-color: #3b82f6;
        }

        /* 域名扫描设置样式 */
        .config-option small {
            color: #888;
            font-size: 11px;
        }

        .config-controls {
            display: flex;
            gap: 15px;
            align-items: center;
            margin-bottom: 10px;
        }

        .config-controls label {
            font-size: 12px;
            color: #ccc;
        }

        .config-controls select {
            padding: 4px 8px;
            background: rgba(0, 0, 0, 0.5);
            border: 1px solid #444;
            border-radius: 4px;
            color: #fff;
            font-size: 12px;
        }

        /* 进度条 */
        .progress-container {
            margin-top: 10px;
        }

        .progress-text {
            font-size: 12px;
            margin-bottom: 5px;
            color: #ccc;
        }

        .progress-bar-container {
            background: rgba(0, 0, 0, 0.5);
            border-radius: 10px;
            height: 6px;
            overflow: hidden;
        }

        .progress-bar {
            background: linear-gradient(45deg, #00d4aa, #0ea5e9);
            height: 100%;
            width: 0%;
            transition: width 0.3s ease;
        }

        /* API测试区域 */
        .api-test-section {
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 8px;
            padding: 15px;
            margin-bottom: 15px;
            transition: all 0.3s;
        }

        .api-test-section:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.2);
        }

        .test-controls {
            display: flex;
            gap: 10px;
            margin-bottom: 10px;
        }

        .test-controls select {
            flex: 1;
            padding: 8px;
            border-radius: 6px;
            background: rgba(0, 0, 0, 0.5);
            color: #fff;
            border: 1px solid #444;
            font-size: 12px;
        }

        /* 结果显示区域 */
        .results-container {
            max-height: 300px;
            overflow-y: auto;
            overflow-x: hidden;
        }

        .category {
            margin-bottom: 15px;
            background: rgba(0, 0, 0, 0.2);
            border: 1px solid rgba(255, 255, 255, 0.1);
            border-radius: 8px;
            padding: 12px;
            transition: all 0.3s;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .category:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.2);
            background: rgba(0, 0, 0, 0.3);
        }

        .category-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            cursor: pointer;
            margin-bottom: 8px;
        }

        .category-title {
            font-weight: 600;
            font-size: 14px;
            color: #fff;
        }

        .count-badge {
            background: linear-gradient(45deg, #60a5fa, #3b82f6);
            color: #0b1220;
            padding: 2px 8px;
            border-radius: 12px;
            font-size: 12px;
            font-weight: 600;
        }

        .category-content {
            max-height: 120px;
            overflow-y: auto;
            overflow-x: hidden;
            font-size: 12px;
            width: 100%;
            box-sizing: border-box;
        }

        .category-content.collapsed {
            display: none;
        }

        .item {
            padding: 6px 10px;
            margin: 3px 0;
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 4px;
            word-break: break-all;
            cursor: pointer;
            transition: all 0.3s ease;
            color: #ccc;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .item:hover {
            background: rgba(255, 255, 255, 0.1);
            border-color: #3b82f6;
            color: #fff;
            transform: translateX(3px);
        }

        .item::after {
            content: '';
            position: absolute;
            left: -100%;
            top: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
            transition: all 0.5s;
        }

        .item:hover::after {
            left: 100%;
        }

        .item.copied {
            background: rgba(59, 130, 246, 0.16);
            border-color: #3b82f6;
            color: #fff;
        }

        /* 加载状态 */
        .loading {
            text-align: center;
            padding: 20px;
            color: #00d4aa;
        }

        .loading::after {
            content: '';
            display: inline-block;
            width: 20px;
            height: 20px;
            border: 2px solid rgba(0, 212, 170, 0.3);
            border-top: 2px solid #00d4aa;
            border-radius: 50%;
            animation: spin 1s linear infinite;
            margin-left: 10px;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        /* 统计信息 */
        .stats {
            text-align: center;
            margin-top: 15px;
            padding: 10px;
            background: rgba(0, 0, 0, 0.3);
            border-radius: 6px;
            font-size: 12px;
            color: #ccc;
            transition: all 0.3s;
            border: 1px solid rgba(255, 255, 255, 0.05);
        }

        .stats:hover {
            background: rgba(0, 0, 0, 0.4);
            border-color: rgba(255, 255, 255, 0.1);
            transform: translateY(-2px);
        }

        /* 滚动条样式 */
        /* 隐藏数字输入框的加减按钮 */
        input[type="number"]::-webkit-outer-spin-button,
        input[type="number"]::-webkit-outer-spin-button,
        input[type="number"]::-webkit-inner-spin-button {
            -webkit-appearance: none;
            appearance: none;
            margin: 0;
        }

        input[type="number"] {
            -webkit-appearance: textfield;
            -moz-appearance: textfield;
            appearance: textfield;
        }

        /* 滚动条样式 */
        ::-webkit-scrollbar {
            width: 6px;
        }

        ::-webkit-scrollbar-track {
            background: transparent;
            border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb {
            background: rgba(255, 255, 255, 0.6);
            border-radius: 4px;
        }
        ::-webkit-scrollbar-thumb:hover {
            background: rgba(255, 255, 255, 0.8);
        }

        /* 模态框样式 */
        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.8);
            z-index: 1000;
        }

        .modal-content {
            position: relative;
            width: 90%;
            max-width: 600px;
            max-height: 80%;
            background: #1a1a2e;
            margin: 50px auto;
            padding: 20px;
            border-radius: 8px;
            border: 1px solid #444;
            overflow-y: auto;
        }

        .modal-close {
            position: absolute;
            top: 10px;
            right: 15px;
            background: none;
            border: none;
            color: #fff;
            font-size: 24px;
            cursor: pointer;
        }

        .modal h3 {
            color: #00d4aa;
            margin-bottom: 15px;
        }

        /* 导出弹窗特殊样式 */
        .export-modal-content {
            max-width: 500px;
        }

        .export-modal-body {
            padding: 0;
        }

        .export-modal-body p {
            margin-bottom: 20px;
            color: #ccc;
            text-align: center;
        }

        .export-buttons {
            display: flex;
            gap: 15px;
            justify-content: center;
        }

        .export-option {
            flex: 1;
            padding: 15px;
            text-align: center;
            cursor: pointer;
            transition: all 0.2s ease;
            background: linear-gradient(45deg, #00d4aa, #0ea5e9);
        }

        .export-option.export-csv {
            background: linear-gradient(45deg, #27ae60, #2ecc71);
        }

        .export-option small {
            opacity: 0.9;
            font-size: 0.8em;
            display: block;
            margin-top: 5px;
        }

        .export-option:hover {
            transform: translateY(-2px);
        }

        /* 当前域名显示 */
        .current-domain {
            margin-top: 10px;
            padding: 8px;
            background: rgba(0, 0, 0, 0.3);
            border-radius: 6px;
            font-size: 12px;
            color: #ccc;
        }

        /* 设置页面样式 */
        .headers-controls {
            margin-bottom: 15px;
        }

        .header-input-group {
            display: flex;
            gap: 8px;
            margin-bottom: 10px;
            align-items: flex-start;
        }

        .header-input-group input[type="text"] {
            padding: 8px;
            background: rgba(0, 0, 0, 0.5);
            border: 1px solid #444;
            border-radius: 6px;
            color: #fff;
            font-size: 12px;
        }

        .header-key-input {
            flex: 1;
            min-width: 120px;
        }

        .header-value-input {
            flex: 2;
            min-width: 200px;
        }

        .remove-header-btn {
            padding: 8px 12px;
            background: rgba(231, 76, 60, 0.3);
            border: 1px solid rgba(231, 76, 60, 0.5);
            border-radius: 6px;
            color: #fff;
            cursor: pointer;
            font-size: 12px;
            transition: all 0.2s ease;
            min-width: 60px;
        }

        .remove-header-btn:hover {
            background: rgba(231, 76, 60, 0.5);
            border-color: rgba(231, 76, 60, 0.7);
            transform: translateY(-1px);
        }

        .headers-actions {
            display: flex;
            gap: 10px;
            margin-bottom: 10px;
        }

        .headers-actions .action-button {
            flex: 1;
        }

        #headersContainer {
            margin-bottom: 15px;
        }

        .header-input-group input[type="text"]:focus {
            outline: none;
            border-color: #00d4aa;
            box-shadow: 0 0 5px rgba(0, 212, 170, 0.3);
        }

        /* 脚本项布局调整 */
        .script-item {
            display: flex;
            align-items: center;
            padding: 8px;
            margin: 4px 0;
            background: rgba(255, 255, 255, 0.05);
            border-radius: 4px;
            border: 1px solid rgba(255, 255, 255, 0.1);
        }

        .script-name {
            flex: 1;
            color: #fff;
            font-size: 12px;
            margin-right: 8px;
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
            max-width: 120px; /* 限制脚本名称宽度 */
        }

        /* 按钮容器 */
        .script-actions {
            display: flex;
            gap: 4px;
            flex-shrink: 0; /* 防止按钮被压缩 */
        }

        /* 统一的按钮样式 */
        .inject-btn, .modify-btn, .delete-btn {
            padding: 4px 8px;
            border: 1px solid rgba(255, 255, 255, 0.2);
            border-radius: 3px;
            cursor: pointer;
            font-size: 10px;
            transition: all 0.2s ease;
            min-width: 36px;
            height: 22px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
            text-align: center;
            background: rgba(255, 255, 255, 0.1);
            color: #fff;
            flex-shrink: 0;
        }

        /* 悬停效果 */
        .inject-btn:hover, .modify-btn:hover, .delete-btn:hover {
            background: rgba(255, 255, 255, 0.2);
            border-color: rgba(255, 255, 255, 0.3);
            transform: translateY(-1px);
        }

        /* 激活状态 */
        .inject-btn:active, .modify-btn:active, .delete-btn:active {
            transform: translateY(0);
            background: rgba(255, 255, 255, 0.15);
        }

        .regex-config {
            margin-bottom: 15px;
        }

        .regex-item {
            margin-bottom: 15px;
        }

        .regex-item label {
            display: block;
            font-size: 12px;
            color: #ccc;
            margin-bottom: 5px;
        }

        .regex-item textarea {
            width: 100%;
            padding: 8px;
            background: rgba(0, 0, 0, 0.5);
            border: 1px solid #444;
            border-radius: 6px;
            color: #fff;
            font-size: 12px;
            resize: vertical;
            min-height: 40px;
        }

        .regex-actions {
            display: flex;
