        .action-button .border-br {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-top: none;
            position: absolute;
            height: 20%;
            border-radius: 0 0 8px 0;
            right: 0;
            bottom: 0;
        }
        /* 弱化霓虹装饰 */
