            color: #ccc;
            font-size: 12px;
            cursor: pointer;
            transition: all 0.3s ease;
            display: inline-flex;
            align-items: center;
            gap: 5px;
        }

        .toggle-button:hover {
            background: rgba(0, 212, 170, 0.2);
            border-color: #00d4aa;
            color: #00d4aa;
            transform: translateY(-1px);
        }

        .toggle-icon {
            font-size: 10px;
            transition: transform 0.3s ease;
        }

        .toggle-button.collapsed .toggle-icon {
            transform: rotate(180deg);
        }

        .scan-buttons-container {
            transition: all 0.3s ease;
            overflow: hidden;
        }

        .scan-buttons-container.collapsed {
            max-height: 0;
            opacity: 0;
            margin: 0;
            padding: 0;
        }

        /* 结果容器动态高度调整 */
        .results-container {
            max-height: 300px;
            overflow-y: auto;
            overflow-x: hidden;
            transition: max-height 0.3s ease;
