            word-break: break-all;
            cursor: pointer;
            transition: all 0.3s ease;
            color: #ccc;
            position: relative;
            overflow: hidden;
            width: 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        .item:hover {
            background: rgba(255, 255, 255, 0.1);
            border-color: #3b82f6;
