        .action-button .border-tr {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 0 8px 0 0;
            top: 0;
            right: 0;
        }

        .action-button:hover .border-tr {
