        .action-button .shine {
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            width: 42px;
            background: rgba(255, 255, 255, 0.2);
            filter: blur(0);
            transform: skew(-13deg) translateX(-120%);
