        /* Header mini buttons - gradient swipe style */
        .btn {
          --color: #00A97F;
          --color2: #212121;
          padding: 0.45em 0.9em;
          background-color: transparent;
          border-radius: 10px;
          border: 1.5px solid var(--color);
          transition: .5s ease;
          position: relative;
          overflow: hidden;
          cursor: pointer;
          z-index: 1;
          font-weight: 500;
          font-size: 12px;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          text-transform: none;
          color: var(--color);
          line-height: 1;
        }
        .btn::before {
          content: '';
          display: block;
          height: 110%;
          width: 310%;
          transform: translate(0, -50%);
          position: absolute;
          top: 50%;
          left: -5%;
          z-index: -1;
          transition: .7s;
          background: -moz-linear-gradient(45deg, var(--color2) 33%, var(--color) 66%);
          background: -webkit-linear-gradient(45deg, var(--color2) 33%, var(--color) 66%);
          background: linear-gradient(45deg, var(--color2) 33%, var(--color) 66%);
        }
        .btn:hover::before {
          left: -200%;
        }
        .btn:hover {
          color: var(--color2);
        }
        .btn:active {
          transform: scale(.98);
          filter: brightness(.9);
        }
        /* JS注入页面样式 */
        .script-info {
            font-size: 11px;
            color: #888;
            margin-left: 10px;
        }



        .header-input-group {
            display: flex;
