        }

* {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            width: 480px;
            min-height: 650px;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: rgb(30, 30, 30);
            color: #fff;
            overflow-x: hidden;
        }

        .main-container {
            width: 100%;
            min-height: 650px;
            border-radius: 10px;
            padding: 1px;
            background: rgb(30, 30, 30);
            position: relative;
        }



        @keyframes moveDot {
            0%, 100% { top: 10%; right: 10%; }
            25% { top: 10%; right: calc(100% - 35px); }
            50% { top: calc(100% - 30px); right: calc(100% - 35px); }
            75% { top: calc(100% - 30px); right: 10%; }
        }

        .card {
            z-index: 1;
            width: 100%;
            height: 100%;
            min-height: 648px;
            border-radius: 10px;
            background: rgb(45, 45, 45);
            position: relative;
            color: #fff;
            overflow: hidden;
            box-shadow: 0px 0px 10px 5px rgba(0, 0, 0, 0.705);
            transition: all 0.4s;
        }

        .card:hover {
            border-radius: 15px;
            box-shadow: 0px 0px 15px 8px rgba(0, 0, 0, 0.8);
            background: rgb(30, 30, 30);
        }

        /* 内容切换效果 */
        .card .first-content {
            position: relative;
            height: 100%;
            width: 100%;
            z-index: 1;
            transition: all 0.4s;
        }

        .card::after {
            content: "";
            position: absolute;
            width: 260px;
            height: 200px;
            left: -30%;
            top: -30%;
            background: #ffffff;
            filter: blur(60px);
            opacity: 0.4;
            z-index: 0;
            pointer-events: none;
            transition: all 0.4s;
        }

        .card:hover::after {
            opacity: 0.6;
            width: 280px;
            height: 220px;
            filter: blur(70px);
        }







        .bottoml {
            bottom: 10%;
        }

        .leftl {
            left: 10%;
            width: 1px;
            height: 100%;
            background: linear-gradient(180deg, #747474 30%, #222424 70%);
        }

        .rightl {
            right: 10%;
            width: 1px;
            height: 100%;
        }

        /* 标题区域 */
        .header {
            text-align: center;
            padding: 12px 15px 10px;
            position: relative;
            z-index: 10;
        }

        .header h1 {
            font-size: 20px;
            font-weight: bolder;
            background: linear-gradient(45deg, rgb(30, 30, 30) 4%, #fff, rgb(30, 30, 30));
            background-clip: text;
            -webkit-background-clip: text;
            color: transparent;
            margin-bottom: 3px;
        }

        .author-info {
            font-size: 11px;
            color: #888;
            opacity: 0.8;
        }

        /* 横向导航 */
        .nav-tabs {
            display: flex;
            justify-content: center;
            margin: 0 15px 20px;
            background: rgba(255, 255, 255, 0.04);
            border: 1px solid rgba(255, 255, 255, 0.08);
            border-radius: 10px;
            padding: 6px;
            position: relative;
            z-index: 10;
        }

        .nav-tab {
            flex: 1;
            padding: 8px 12px;
            text-align: center;
            font-size: 12px;
            font-weight: 500;
            cursor: pointer;
            border-radius: 6px;
            transition: all 0.3s ease;
            color: #888;
            position: relative;
            overflow: hidden;
        }

        .nav-tab.active {
            background: rgba(255, 255, 255, 0.08);
            color: #fff;
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
            border: 1px solid rgba(255, 255, 255, 0.12);
        }

        .nav-tab:hover {
            color: #fff;
            background: rgba(255, 255, 255, 0.05);
            transform: translateY(-1px);
        }

        .nav-tab::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 50%;
            width: 0;
            height: 2px;
            background: linear-gradient(90deg, transparent, #3b82f6, transparent);
            transition: all 0.3s;
        }

        .nav-tab:hover::after {
            width: 80%;
            left: 10%;
        }

        .nav-tab.active::after {
            width: 90%;
            left: 5%;
            background: linear-gradient(90deg, transparent, #00d4aa, transparent);
        }

        /* 页面内容区域 */
        .page-content {
            padding: 0 15px;
            position: relative;
            z-index: 10;
        }

        .page {
            display: none;
        }

        .page.active {
            display: block;
        }

        .action-button {
            position: relative;
            cursor: pointer;
            padding: 12px 16px;
            text-align: center;
            display: inline-flex;
            justify-content: center;
            align-items: center;
            font-size: 14px;
            font-weight: 600;
            color: #fff;
            border-radius: 10px;
            border: 1px solid rgba(255, 255, 255, 0.2);
            background: rgba(31, 41, 55, 0.3);
            backdrop-filter: blur(12px);
            transition: transform 0.3s ease, box-shadow 0.3s ease, background 0.3s ease;
            width: 100%;
            margin-bottom: 10px;
            overflow: hidden;
            will-change: transform;
        }

        .action-button:focus {
            outline: 2px solid white;
            outline-offset: 4px;
        }

        .action-button span.text {
            position: relative;
            z-index: 20;
        }

        .action-button .shine {
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            width: 42px;
            background: rgba(255, 255, 255, 0.2);
            filter: blur(0);
            transform: skew(-13deg) translateX(-120%);
            transition: transform 1s ease;
            z-index: 10;
            pointer-events: none;
        }

        .action-button:hover .shine {
            transform: skew(-13deg) translateX(120%);
        }
        .action-button:hover {
            transform: scale(1.08);
            box-shadow: 0 20px 25px -5px rgba(107, 114, 128, 0.5);
        }

        .action-button .border-tl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 8px 0 0 0;
            top: 0;
            left: 0;
        }

        .action-button .border-tr {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 0 8px 0 0;
            top: 0;
            right: 0;
        }

        .action-button:hover .border-tr {
            height: 90%;
        }

        .action-button .border-bl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-top: none;
            position: absolute;
            height: 90%;
            border-radius: 0 0 0 8px;
            left: 0;
            bottom: 0;
        }

        .action-button:hover .border-bl {
            height: 90%;
        }

        .action-button .border-br {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-top: none;
            position: absolute;
            height: 20%;
            border-radius: 0 0 8px 0;
            right: 0;
            bottom: 0;
        }
        /* 弱化霓虹装饰 */
        .action-button .border-tl,
        .action-button .border-tr,
        .action-button .border-bl,
        .action-button .border-br {
            display: none !important;
        }

        /* 扫描按钮特殊样式 */
        .scan-button {
            background: #3b82f6;
            border-color: rgba(59, 130, 246, 0.5);
        }
        .scan-button:hover {
            background: #2563eb;
        }
        .scan-button.scanning {
            animation: bluepulse 2s infinite;
        }
        @keyframes bluepulse {
            0% { box-shadow: 0 0 5px rgba(59,130,246,0.25); }
            50% { box-shadow: 0 0 18px rgba(59,130,246,0.45); }
            100% { box-shadow: 0 0 5px rgba(59,130,246,0.25); }
        }

        /* 深度扫描配置 */
        .deep-scan-config {
            background: rgba(40, 40, 40, 0.5);
            border: 1px solid rgba(90, 90, 90, 0.3);
            border-radius: 8px;
            padding: 15px;
