            border-color: rgba(255, 255, 255, 0.2);
        }

        .test-controls {
            display: flex;
            gap: 10px;
            margin-bottom: 10px;
        }

        .test-controls select {
            flex: 1;
            padding: 8px;
            border-radius: 6px;
