        /* 内容切换效果 */
        .card .first-content {
            position: relative;
            height: 100%;
            width: 100%;
            z-index: 1;
            transition: all 0.4s;
        }

        .card::after {
            content: "";
            position: absolute;
            width: 260px;
            height: 200px;
            left: -30%;
            top: -30%;
            background: #ffffff;
            filter: blur(60px);
            opacity: 0.4;
            z-index: 0;
            pointer-events: none;
            transition: all 0.4s;
        }

        .card:hover::after {
            opacity: 0.6;
            width: 280px;
            height: 220px;
            filter: blur(70px);
        }







