        .nav-tab::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 50%;
            width: 0;
            height: 2px;
            background: linear-gradient(90deg, transparent, #3b82f6, transparent);
            transition: all 0.3s;
        }

        .nav-tab:hover::after {
            width: 80%;
            left: 10%;
