        .action-button .shine {
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            width: 42px;
            background: rgba(255, 255, 255, 0.2);
            filter: blur(0);
            transform: skew(-13deg) translateX(-120%);
            transition: transform 1s ease;
            z-index: 10;
            pointer-events: none;
        }

        .action-button:hover .shine {
            transform: skew(-13deg) translateX(120%);
        }
        .action-button:hover {
            transform: scale(1.08);
            box-shadow: 0 20px 25px -5px rgba(107, 114, 128, 0.5);
        }

        .action-button .border-tl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 8px 0 0 0;
            top: 0;
            left: 0;
        }

        .action-button .border-tr {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-bottom: none;
            position: absolute;
            height: 90%;
            border-radius: 0 8px 0 0;
            top: 0;
            right: 0;
        }

        .action-button:hover .border-tr {
            height: 90%;
        }

        .action-button .border-bl {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-right: none;
            border-top: none;
            position: absolute;
            height: 90%;
            border-radius: 0 0 0 8px;
            left: 0;
            bottom: 0;
        }

        .action-button:hover .border-bl {
            height: 90%;
        }

        .action-button .border-br {
            width: 50%;
            transition: all 0.3s;
            border: 2px solid #D4EDF9;
            border-left: none;
            border-top: none;
            position: absolute;
            height: 20%;
            border-radius: 0 0 8px 0;
            right: 0;
            bottom: 0;
        }
        /* 弱化霓虹装饰 */
        .action-button .border-tl,
